function deleteInfo(){document.getElementById("info")&&setTimeout(()=>{document.getElementById("info").style.display="none"},3e3)}function checkRegex(){if(document.querySelectorAll(".needRegex")){document.querySelectorAll(".needRegex").forEach(e=>{const t=e.previousElementSibling;e.addEventListener("change",()=>{e.checkValidity()?t.classList.remove("incorrect"):t.classList.add("incorrect")})})}}function copyToClipboard(e){e.forEach(e=>{e.addEventListener("click",()=>{const t=document.createElement("input"),n=e.attributes.data.value;t.setAttribute("value",n),document.body.appendChild(t),t.select(),document.execCommand("copy"),document.body.removeChild(t)})})}